tinymce.PluginManager.add('openixemailtag', function(editor, url) {

    // Add a button that opens a window
    editor.addButton('openixemailtag', {
        text: 'Email Tags',
        icon: false,
        type: 'menubutton',
        menu: [
            {
                text: 'Name',
                value: '#name#',
                onclick: function() {
                    editor.insertContent(this.value());
                }
            },
            {
                text: 'Date',
                value: '#date#',
                onclick: function() {
                    editor.insertContent(this.value());
                }
            }
        ]
    });

});